// module to extend the functionality of the car module
// by means of a service locator

module.exports = (serviceLocator) => {
    // fetch car module from service locator
    const car = serviceLocator.get('car');

    // create a proxy for the car start function
    const carStartOld = car.start;
    car.start = function(key, callback) {
        console.log('Proxy start car');
        carStartOld(key, callback);
    }

    // add a new function to the car library
    car.hoot = () => {
        console.log('Proxy hoot');
        console.log('hoot hoot');
    }

};

